CREATE TABLE `allowed_workspace_mapping` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `workspace_id` INT(11) NOT NULL , `allowed_workspace` VARCHAR(80) NOT NULL , `status` ENUM('ENABLED','DISABLED') NOT NULL DEFAULT 'ENABLED' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`), UNIQUE KEY (`workspace_id`,`allowed_workspace`), KEY (`allowed_workspace`));
